tinymce.PluginManager.add("textmark",
function(a) {
    a.addCommand("textmark",
    function() {
        //a.execCommand("mceInsertContent", !1, "<hr />")
        a.execCommand("mceReplaceContent", !1, "<mark>{$selection}</mark>")
    }),
    a.addButton("textmark", {
        icon: "backcolor",
        tooltip: "标记文字",
        cmd: "textmark"
    })
});